import React, { useState } from "react";
import {secondsToDuration, minutesToDuration} from "../utils/duration/index.js"

function Countdown({session, focusDuration, breakDuration, isTimerRunning}) {

    const timerText = (session?.label === "Focusing") ? focusDuration : breakDuration;

    const progressBar = (secondsRemaining) => {
        if (session?.label === "Focusing") {
          const secondsElapsed = focusDuration*60 - secondsRemaining;
          const percentFill = secondsElapsed/focusDuration
          return percentFill;
        }
         const secondsElapsed = breakDuration*60-secondsRemaining;
         const percentFill = secondsElapsed/breakDuration;
         return percentFill;
      }

    return (
<div hidden={!session}>
        {/* TODO: This area should show only when there is an active focus or break - i.e. the session is running or is paused */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
            <h2 data-testid="session-title">
              {session?.label} for {timerText}:00 minutes
            </h2>
            {/* TODO: Update message below correctly format the time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {secondsToDuration(session?.timeRemaining)} remaining
            </p>
            <p hidden={!(session && !isTimerRunning)}>PAUSED</p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow= {progressBar(session?.timeRemaining)} // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: `${progressBar(session?.timeRemaining)}%` }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    )
}

export default Countdown;
